      }
    },
    validateEmail() {
      if (!this.employee.Email) {
        this.errors.email = "Email Không được để trống!";
      } else if (this.employee.Email && !this.isEmail(this.employee.Email)) {
        this.errors.email = "Email Không đúng định dạng!";
      } else {
        this.errors.email = "";
      }
