import axios from "axios";
import $ from "jquery";
import Paginate from "vuejs-paginate-next";
import { useToast } from "vue-toastification";
// import Combobox from "../base/BaseCombobox2.vue";
export default {
  data() {
    return {
      isOpenPopupTrain: false,
      isOpenPopupFostering: false,
      isOpenPopupEducation: false,
      isOpenPopupFormAddEducation: false,
      isOpenPopupFormEditEducation: false,
      isOpenPopupFormAddTrain: false,
      isOpenPopupFormEditTrain: false,
      isOpenPopupFormAddFostering: false,
      isOpenPopupFormEditFostering: false,
      pageDefault: 10,
      pageNumber: 1,
      txtSearch: "",
      totalPage: 1,
      totalRecord: 0,
      isShowDrop: false,
      dataSinhVienPunish: [],
      dataSinhVienBonus: {},
      isActive: "20",
      page: 1,
      employees: {},
      isShow: false,
      department: {},
      isShowLoad: false,
      position: {},
      departmentID: "",
      positionID: "",
      employeeSelect: {},
      newCode: "",
      formMode: 1,
      employeeID: "",
      isShowPopup: false,
      empCodeDelete: "",
      empID: ""
    };
  },
  components: {
    Paginate
  },
  watch: {
    txtSearch: function () {
      if (this.txtSearch == "") {
        this.getPagingEmployee();
      }
    }
  },
  created() {
    this.getPagingEmployee();
    this.getDepartment();
    this.getPosition();
  },
  methods: {
    SavePunish() {
      var me = this;
      const toast = useToast();
      try {
        axios.post("https://localhost:7029/api/Discipline", this.dataSinhVienPunish).then(function (res) {
          console.log(res);
          toast.success("thêm dữ liệu thành công", {
            timeout: 2000
          });
          me.isOpenPopupEducation = false;
        }).catch(function () {
          console.log("error");
          toast.error("thêm dữ liệu thất bại", {
            timeout: 2000
          });
        });
      } catch (error) {
        console.log(error);
      }
    },
    SaveBonus() {
      var me = this;
      const toast = useToast();
      try {
        axios.post("https://localhost:7029/api/Bonus", this.dataSinhVienBonus).then(function (res) {
          console.log(res);
          toast.success("thêm dữ liệu thành công", {
            timeout: 2000
          });
          me.isOpenPopupTrain();
        }).catch(function () {
          console.log("error");
          toast.error("thêm dữ liệu thất bại", {
            timeout: 2000
          });
        });
      } catch (error) {
        console.log(error);
      }
    },
    thongbao() {
      alert("Xác nhận đóng");
    },
    OpenPopupTrain(item) {
      this.isOpenPopupTrain = true;
      this.dataSinhVienBonus = item;
    },
    OpenPopupFostering(item) {
      this.isOpenPopupFostering = true;
      this.dataSinhVienBonus = item;
    },
    OpenPopupEducation(item) {
      this.isOpenPopupEducation = true;
      this.dataSinhVienPunish = item;
    },
    OpenPopupFormAddEducation(item) {
      this.isOpenPopupFormAddEducation = true;
      this.dataSinhVienPunish = item;
    },
    OpenPopupFormEditEducation(item) {
      this.isOpenPopupFormEditEducation = true;
      this.dataSinhVienPunish = item;
    },
    OpenPopupFormAddTrain(item) {
      this.isOpenPopupFormAddTrain = true;
      this.dataSinhVienPunish = item;
    },
    OpenPopupFormEditTrain(item) {
      this.isOpenPopupFormEditTrain = true;
      this.dataSinhVienPunish = item;
    },
    OpenPopupFormAddFostering(item) {
      this.isOpenPopupFormAddFostering = true;
      this.dataSinhVienPunish = item;
    },
    OpenPopupFormEditFostering(item) {
      this.isOpenPopupFormEditFostering = true;
      this.dataSinhVienPunish = item;
    },
    deleteEmployee(emp) {
      this.isShowPopup = !this.isShow;
      this.empCodeDelete = emp.EmployeeCode;
      this.empID = emp.EmployeeID;
    },
    deleteEmp(value) {
      this.isShowPopup = value;
      var me = this;
      const toast = useToast();
      try {
        axios.delete(`https://localhost:7029/api/Employees/${me.empID}`).then(function (res) {
          console.log(res);
          toast.success("Xóa dữ liệu thành công", {
            timeout: 2000
          });
          me.getPagingEmployee();
        }).catch(function () {
          toast.error("xóa dữ liệu thất bại", {
            timeout: 2000
          });
          console.log(1);
        });
      } catch (error) {
        console.log(error);
      }
    },
    editEmployee(emp) {
      this.employeeSelect = emp;
      this.employeeID = emp.EmployeeID;
      this.isShow = true;
      this.formMode = 2;
    },
    getPagingEmployee() {
      try {
        var me = this;
        me.isShowLoad = true;
        axios.get(`https://localhost:7029/api/Employees/Filter?keyword=${this.txtSearch}&pageSize=${this.pageDefault}&departmentID=${this.departmentID}&positionID=${this.positionID}&pageNumber=${this.pageNumber}`).then(function (res) {
          me.totalPage = res.data.TotalPages;
          me.totalRecord = res.data.TotalRecords;
          me.employees = res.data.Data;
          console.log(me.totalPage);
          console.log(me.employees);
          me.isShowLoad = false;
        }).catch(function () {
          console.log(1);
        });
      } catch (error) {
        console.log(error);
      }
    },
    getDepartment() {
      try {
        var me = this;
        axios.get("https://localhost:7029/api/Departments").then(function (res) {
          me.department = res.data;
        }).catch(function () {
          console.log(1);
        });
      } catch (error) {
        console.log(error);
      }
    },
    getPosition() {
      try {
        var me = this;
        axios.get("https://localhost:7029/api/Position").then(function (res) {
          me.position = res.data;
        }).catch(function () {
          console.log(1);
        });
      } catch (error) {
        console.log(error);
      }
    },
    formatDate(date) {
      try {
        if (date) {
          date = new Date(date);
          let newDate = date.getDate();
          let month = date.getMonth() + 1;
          let year = date.getFullYear();
          newDate = newDate < 10 ? `0${newDate}` : newDate;
          month = month < 10 ? `0${month}` : month;
          return `${newDate}-${month}-${year}`;
        }
      } catch (error) {
        return "";
      }
    },
    selectItemCbb(value) {
      if (value.DepartmentID) {
        this.departmentID = value.DepartmentID;
      } else {
        this.departmentID = "";
        this.getPagingEmployee();
      }
      this.getPagingEmployee();
    },
    selectItemCategory(value) {
      console.log(value.PositionsID);
      if (value.PositionsID) {
        this.positionID = value.PositionsID;
      } else {
        this.positionID = "";
        this.getPagingEmployee();
      }
      this.getPagingEmployee();
    },
    gender(gender) {
      switch (gender) {
        case 0:
          gender = "Nam";
          break;
        case 1:
          gender = "Nữ";
          break;
        default:
          break;
      }
      return gender;
    },
    getNewCode() {
      try {
        var me = this;
        axios.get("https://localhost:7029/api/Employees/NewCode").then(function (res) {
          me.newCode = res.data;
        }).catch(function () {
          console.log(1);
        });
      } catch (error) {
        console.log(error);
      }
    },
    btnShow() {
      this.employeeSelect = {};
      this.getNewCode();
      this.formMode = 1;
      this.isShow = !this.isShow;
    },
    showPage(is) {
      this.isShowDrop = is;
    },
    btnDropUp() {
      $(".icon-dropup").toggleClass("iconrotate");
      if (!this.isShowDrop) {
        this.showPage(true);
        $(".icon-dropup").addClass("iconrotate");
      } else {
        this.showPage(false);
        $(".icon-dropup").removeClass("iconrotate");
      }
    },
    getPageDefault(e) {
      this.isActive = e.target.getAttribute("pageSize");
      this.pageDefault = e.target.getAttribute("pageSize");
      this.showPage(false);
      $(".icon-dropup").removeClass("iconrotate");
      this.getPagingEmployee();
      if (this.pageDefault > this.totalRecord) {
        this.pageDefault = this.totalRecord;
      }
    },
    clickCallback(pageNum) {
      this.pageNumber = pageNum;
      this.getPagingEmployee();
    }
  }
};
