import Paginate from "vuejs-paginate-next";
import axios from "axios";
import Form from "../base/FormEmployee.vue";
import $ from "jquery";
import Combobox from "../base/BaseCombobox2.vue";
import popUp from "../base/BasePopupDelete.vue";
import { useToast } from "vue-toastification";
export default {
  components: {
    Paginate,
    Form,
    Combobox,
    popUp
  },
  data() {
    return {
      isOpenPopupCurriculumVitae: false,
      isOpenPopupFormEditCurriculumVitae: false,
      isOpenPopupState: false,
      isOpenPopupAddState: false,
      isOpenPopupEditState: false,
      isActive: "20",
      pageNumber: 1,
      page: 1,
      employees: {},
      totalPage: 1,
      isShow: false,
      //gán v-show=isShow hoặc v-show =false để ẩn form
      isShowDrop: false,
      totalRecord: 0,
      IDKhoa: "",
      pageDefault: 20,
      department: {},
      isShowLoad: false,
      position: {},
      departmentID: "",
      positionID: "",
      txtSearch: " ",
      employeeSelect: {},
      newCode: "",
      formMode: 1,
      employeeID: "",
      isShowPopup: false,
      empCodeDelete: "",
      empID: ""
    };
  },
  created() {
    this.getPagingEmployee();
    this.getDepartment();
    this.getPosition();
  },
  watch: {
    txtSearch: function () {
      if (this.txtSearch == "") {
        this.getPagingEmployee();
      }
    }
  },
  methods: {
    OpenPopupCurriculumVitae(item) {
      this.isOpenPopupCurriculumVitae = true;
      this.dataSinhVienPunish = item;
    },
    OpenPopupFormEditCurriculumVitae(item) {
      this.isOpenPopupFormEditCurriculumVitae = true;
      this.dataSinhVienPunish = item;
    },
    OpenPopupState(item) {
      this.isOpenPopupState = true;
      this.dataSinhVienPunish = item;
    },
    OpenPopupAddState(item) {
      this.isOpenPopupAddState = true;
      this.dataSinhVienPunish = item;
    },
    OpenPopupEditState(item) {
      this.isOpenPopupEditState = true;
      this.dataSinhVienPunish = item;
    },
    deleteEmployee(emp) {
      this.isShowPopup = !this.isShow;
      this.empCodeDelete = emp.EmployeeCode;
      this.empID = emp.EmployeeID;
    },
    getExcel() {
      try {
        var me = this;
        me.isShowLoad = true;
        axios.get(`https://localhost:44301/api/nhanvien/ExportExcel?keyword=${this.txtSearch}&pageSize=${this.pageDefault}&IDKhoa=${this.IDKhoa}&pageNumber=${this.pageNumber}`).then(function (res) {
          console.log(res);
        }).catch(function () {
          console.log(1);
        });
      } catch (error) {
        console.log(error);
      }
    },
    deleteEmp(value) {
      this.isShowPopup = value;
      var me = this;
      const toast = useToast();
      try {
        axios.delete(`https://localhost:7029/api/Employees/${me.empID}`).then(function (res) {
          console.log(res);
          toast.success("Xóa dữ liệu thành công", {
            timeout: 2000
          });
          me.getPagingEmployee();
        }).catch(function () {
          toast.error("xóa dữ liệu thất bại", {
            timeout: 2000
          });
          console.log(1);
        });
      } catch (error) {
        console.log(error);
      }
    },
    editEmployee(emp) {
      this.employeeSelect = emp;
      this.employeeID = emp.EmployeeID;
      this.isShow = true;
      this.formMode = 2;
    },
    getPagingEmployee() {
      try {
        var me = this;
        me.isShowLoad = true;
        axios.get(`https://localhost:44301/api/nhanvien/Filter?keyword=${this.txtSearch}&pageSize=${this.pageDefault}&IDKhoa=${this.IDKhoa}&pageNumber=${this.pageNumber}`).then(function (res) {
          console.log(res);
          me.totalPage = res.data.TotalPages;
          me.totalRecord = res.data.TotalRecords;
          me.employees = res.data.Data;
          console.log(me.totalPage);
          console.log(me.employees);
          me.isShowLoad = false;
        }).catch(function () {
          console.log(1);
        });
      } catch (error) {
        console.log(error);
      }
    },
    getDepartment() {
      try {
        var me = this;
        axios.get("https://localhost:44301/api/khoa").then(function (res) {
          me.department = res.data;
        }).catch(function () {
          console.log(1);
        });
      } catch (error) {
        console.log(error);
      }
    },
    getPosition() {
      try {
        var me = this;
        axios.get("https://localhost:7029/api/Position").then(function (res) {
          me.position = res.data;
        }).catch(function () {
          console.log(1);
        });
      } catch (error) {
        console.log(error);
      }
    },
    formatDate(date) {
      try {
        if (date) {
          date = new Date(date);
          let newDate = date.getDate();
          let month = date.getMonth() + 1;
          let year = date.getFullYear();
          newDate = newDate < 10 ? `0${newDate}` : newDate;
          month = month < 10 ? `0${month}` : month;
          return `${newDate}-${month}-${year}`;
        }
      } catch (error) {
        return "";
      }
    },
    selectItemCbb(value) {
      if (value.IDKhoa) {
        this.IDKhoa = value.IDKhoa;
      } else {
        this.IDKhoa = "";
        this.getPagingEmployee();
      }
      this.getPagingEmployee();
    },
    selectItemCategory(value) {
      console.log(value.PositionsID);
      if (value.PositionsID) {
        this.positionID = value.PositionsID;
      } else {
        this.positionID = "";
        this.getPagingEmployee();
      }
      this.getPagingEmployee();
    },
    showPage(is) {
      this.isShowDrop = is;
    },
    btnShow() {
      this.employeeSelect = {};
      this.getNewCode();
      this.formMode = 1;
      this.isShow = !this.isShow;
    },
    getNewCode() {
      try {
        var me = this;
        axios.get("https://localhost:7029/api/Employees/NewCode").then(function (res) {
          me.newCode = res.data;
        }).catch(function () {
          console.log(1);
        });
      } catch (error) {
        console.log(error);
      }
    },
    //nhận lệnh ẩn từ bên form chi tiết
    closeForm(value) {
      this.isShow = value;
    },
    btnDropUp() {
      $(".icon-dropup").toggleClass("iconrotate");
      if (!this.isShowDrop) {
        this.showPage(true);
        $(".icon-dropup").addClass("iconrotate");
      } else {
        this.showPage(false);
        $(".icon-dropup").removeClass("iconrotate");
      }
    },
    gender(gender) {
      switch (gender) {
        case 0:
          gender = "Nam";
          break;
        case 1:
          gender = "Nữ";
          break;
        default:
          break;
      }
      return gender;
    },
    getPageDefault(e) {
      this.isActive = e.target.getAttribute("pageSize");
      this.pageDefault = e.target.getAttribute("pageSize");
      this.showPage(false);
      $(".icon-dropup").removeClass("iconrotate");
      this.getPagingEmployee();
      if (this.pageDefault > this.totalRecord) {
        this.pageDefault = this.totalRecord;
      }
    },
    clickCallback(pageNum) {
      this.pageNumber = pageNum;
      this.getPagingEmployee();
    }
  }
};
